<?phpnamespace App\Http\Controllers\Api;use App\Http\Controllers\Controller as Controller;class BaseController extends Controller{    /**     * success response method.     *     * @return \Illuminate\Http\Response     */    public function sendResponse( $result, $message ,$status = true )    {        $response = [            'status'  => $status,            'data'    => $result,            'message' => $message,        ];        return response()->json($response, 200);    }    /**     * return error response.     * @return \Illuminate\Http\Response     */    public function sendError($error, $status = 0, $errorMessages = [] , $code = 200)    {        $response = [            'status' => $status,            'message' => $error,        ];        if(!empty($errorMessages)){            $response['data'] = $errorMessages;        }        return response()->json($response, $code);    }}