<?phpnamespace App\Traits;use App\Models\Seat;trait CheckSeatTrait{    public function getOccupiedSeats($data)    {        $startStation = $data['station_from'] ;        $endStation=  $data['station_to'];        $occupiedSeats = Seat::join('booked_seats', function ( $query ) use ($startStation , $endStation )  {            $query->on('seats.id', 'booked_seats.seat_id') ;        })            ->where(function ($query1) use ($startStation, $endStation) {                $query1->where("booked_seats.station_from","<",$startStation)                    ->where("station_to",">",$endStation);            })            ->orWhere(function ($query) use ($startStation, $endStation) {                $query->where("booked_seats.station_from",">=",$startStation)                    ->where("booked_seats.station_to","<=",$endStation) ;            })            ->orWhere(function ($query3) use ($startStation, $endStation) {                $query3->whereBetween("booked_seats.station_from",[ $startStation, $endStation-1 ])                    ->where("booked_seats.station_to",">",$endStation) ;            })            ->orWhere(function ($query4) use ($startStation, $endStation) {                $query4->where("booked_seats.station_from", "<",  $startStation)                    ->whereBetween("booked_seats.station_to",[ $startStation+1, $endStation ])                ;            })            ->selectRaw('seats.number as chair_number')            ->pluck('chair_number')            ->toArray();        return $occupiedSeats ;    }}